import React from 'react'
import './WeatherConditions.css'

const WeatherConditions = props => {
    return (
        <div>
            {props.error && <small className={classes.Small}>Please enter a valid city.</small>}
            {props.loading && <div className={classes.Loader} />}
            {props.responseObj.cod === 200 ?
                <div>
                    <p><strong>{props.responseObj.name}</strong></p>
                    <p>It is currently {Math.round(props.responseObj.main.temp)} degrees out with {props.responseObj.weather[0].description}.</p>
                </div>
                : null
            }
            {/* {props.error && <small>Please enter a valid city.</small>}
            {props.loading && <div>Loading...</div>}
            {props.forecast.cod === 200 ?
                <div className="weather-conditions">
                    <p><strong>{props.forecast.name}</strong></p>
                    <p>It is currently {Math.round(props.forecast.main.temp)} degrees out with {props.forecast.weather[0].description}.</p>
                </div>
                : null
            } */}
        </div>
    )
}
export default WeatherConditions
